const { ethers } = require("ethers");
require("dotenv").config();

async function deployUniversalNFT() {
    console.log("ðŸš€ Deploying UniversalNFT Contract...");
    
    // Setup provider and signer
    const provider = new ethers.JsonRpcProvider(process.env.POLYGON_RPC_URL);
    const signer = new ethers.Wallet(process.env.PRIVATE_KEY, provider);
    
    console.log("Deployer:", signer.address);
    
    // Get balance
    const balance = await provider.getBalance(signer.address);
    console.log("Balance:", ethers.formatEther(balance), "MATIC");
    
    try {
        // Compile the contract (we'll use the bytecode directly)
        const contractCode = `
        `;
        
        console.log("Deploying contract...");
        
        // Get current gas price
        const feeData = await provider.getFeeData();
        console.log("Current gas price:", ethers.formatUnits(feeData.gasPrice, "gwei"), "gwei");
        
        // Deploy contract
        const factory = new ethers.ContractFactory(
            [], // We'll skip ABI for now, just deploy
            contractCode,
            signer
        );
        
        const contract = await factory.deploy(signer.address, {
            gasLimit: 3000000,
            gasPrice: ethers.parseUnits("40", "gwei")
        });
        
        console.log("Transaction sent:", contract.deploymentTransaction().hash);
        
        // Wait for deployment
        console.log("Waiting for confirmation...");
        await contract.waitForDeployment();
        
        const address = await contract.getAddress();
        
        console.log("âœ… UniversalNFT deployed successfully!");
        console.log("ðŸ“ Contract Address:", address);
        console.log("ðŸ”— PolygonScan:", `https://polygonscan.com/address/${address}`);
        
        return address;
        
    } catch (error) {
        console.error("âŒ Deployment failed:", error.message);
        throw error;
    }
}

deployUniversalNFT()
    .then((address) => {
        console.log("NFT contract ready at:", address);
        process.exit(0);
    })
    .catch((error) => {
        console.error("Error:", error);
        process.exit(1);
    });